import React, { useState } from "react";
import { Login } from "./components/Login";
import { MainApp } from "./components/MainApp";

export default function App(props) {
  //next step goals: (CURRENT) Get the two mongo db's to connect, fix the user-to-server delay issue.

  const [checkLogin, setCheckLogin] = useState(false);

  function handleLogin(check) {
    setCheckLogin(check);
  }

  function checkUserReady() {
    props.onLoad(checkLogin);
  }

  return (
    <div>
      {checkLogin ? (
        <MainApp onLoad={checkUserReady} />
      ) : (
        <Login isLoggedIn={handleLogin} />
      )}
    </div>
  );
}
