import React, { useState } from "react";
import axios from "axios";
import { Greeting } from "./components/Greeting";
import { NewToDo } from "./components/NewToDo";
import { ToDoItem } from "./components/ToDoItem";

export default function App() {
  const [items, setItems] = useState([]);

  axios
    .get("http://localhost:8000")
    .then(function (response) {
      const savedList = response.data;
      console.log(savedList);
      // let itemListArray = Object.values(response.data);
      // console.log(itemListArray[0]);
      // setItems(...itemListArray);
      setItems((prevItems) => {
        return [...prevItems, ...savedList[0].name];
      });
    })
    .catch(function (err) {
      console.log(err);
    });

  function addItem(newItem) {
    let index = 0;
    if (items.length == null) {
      index = 0;
    } else {
      index = items.length - 1;
    }
    axios.post("/", { name: newItem, id: index }).then((response) => {
      console.log(response);
    });
    setItems((prevItems) => {
      return [...prevItems, newItem];
    });
  }

  async function deleteItem(id) {
    await axios.delete("/delete", { id: id });
    alert("item deleted!");
    setItems((prevItems) => {
      return prevItems.filter((item, index) => {
        return index !== id;
      });
    });
  }

  return (
    <div>
      <div className="box">
        <Greeting />
      </div>
      <div className="box">
        <NewToDo onAdd={addItem} />
        <div>
          <ul>
            {[items].map((todoItem, index) => (
              <ToDoItem
                key={index}
                id={index}
                text={todoItem}
                onChecked={deleteItem}
              />
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
}
